mapper.select:
SELECT m.plan_id AS planId, m.maintance_type AS maintanceType, m.plan_name AS planName, first_approval.manip_time AS createTime, m.maintance_desc AS maintanceDesc, COALESCE(first_user.username, '未开始') AS creator, m.start_time AS startTime, m.end_time AS endTime, m.equip_id AS equipId, m.status AS status, COALESCE(last_user.username, '未开始') AS updatePerson, last_approval.manip_time AS updateTime FROM maintance_info m LEFT JOIN (SELECT ai.plan_id, ai.manip_time, ai.applicant_id FROM approval_info ai WHERE ai.manip_time = (SELECT MIN(inside_ai.manip_time) FROM approval_info inside_ai WHERE inside_ai.plan_id = ai.plan_id)) first_approval ON m.plan_id = first_approval.plan_id LEFT JOIN (SELECT ai.plan_id, ai.manip_time, ai.applicant_id FROM approval_info ai WHERE ai.manip_time = (SELECT MAX(inside_ai.manip_time) FROM approval_info inside_ai WHERE inside_ai.plan_id = ai.plan_id)) last_approval ON m.plan_id = last_approval.plan_id LEFT JOIN user_info first_user ON first_approval.applicant_id = first_user.user_id LEFT JOIN user_info last_user ON last_approval.applicant_id = last_user.user_id WHERE 1 = 1
AND m.status = #{maintenancePlanReq.status}
AND m.plan_name LIKE concat('%', #{maintenancePlanReq.planName}, '%')
AND m.start_time >= #{maintenancePlanReq.startTime}
AND m.end_time <= #{maintenancePlanReq.endTime}
LIMIT #{maintenancePlanReq.pageSize} OFFSET #{maintenancePlanReq.offset}
