mapper.select:
select order_info.*,maintance_info.plan_name,maintance_info.end_time planTime
from order_info,maintance_info

    /* 必须条件设置为永真 */

        order_info.plan_id = maintance_info.plan_id


        and order_id like CONCAT('%',#{orderId},'%')


        /* 两张表中都存在开始与结束时间的字段，不指定报错ambiguous，即字段不确定，往前端查错没用 */
        and order_info.start_time > #{startTime} and order_info.end_time < #{endTime}


limit #{limit} offset #{offset}
